import React, { Component } from 'react';
import {View, StyleSheet, TextInput, Button, Alert} from 'react-native';

export default function GetUsers() {

    function getUsersJson() {
        console.log('getUserJson()');


        let xhr = new XMLHttpRequest();

        xhr.open('GET', 'https://localhost:5001/api/Account/ef60986f-c9cd-4b90-8c80-84c30c00de81');

        // Подписываемся на изменение состояния запроса
        xhr.onreadystatechange = function() {
            // Проверка, завершилось ли выполнение запроса?
            if (xhr.readyState !== 4) return;

            if (xhr.status === 200) {
               // Обработка удачного выполнения запроса
               console.log(xhr.responseText);
            } else {
               // Обработка ошибки
               console.log('HTTP error', xhr.status, xhr.statusText);
            }
        };

        // Отправляем запрос
        xhr.send();

    }

    return (

        <View>

            <Button
                title='GET'

                 onPress={getUsersJson}
            />

        </View>
    );
}


const styles = StyleSheet.create({
    form: {
        height: 40,
        borderColor: '#000',
        borderWidth: 1,
        width: 300
    }

});
